create sequence PuestoSeq start with 2 increment by 1 nomaxvalue; 

create trigger PuestoSeqIncrement
before insert on Proyecto_adm.Puesto
for each row
   begin
     select PuestoSeq.nextval into :new.id_Puesto from dual;
   end;
   
   
   
